import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import Grid from "@material-ui/core/Grid";
import Container from "@material-ui/core/Container";
import GitHubIcon from "@material-ui/icons/GitHub";
import FacebookIcon from "@material-ui/icons/Facebook";
import TwitterIcon from "@material-ui/icons/Twitter";
import Header from "../helpers/Header";
import MainFeaturedPost from "../helpers/MainFeaturedPost";
import FeaturedPost from "../helpers/FeaturedPost";
import Main from "../helpers/Main";
import Sidebar from "../helpers/Sidebar";
import Footer from "../helpers/Footer";
import post1 from "../helpers/blog-post.1.md";
import post2 from "../helpers/blog-post.2.md";
import post3 from "../helpers/blog-post.3.md";
import MainCarousel from "../helpers/MainCarousel";

const useStyles = makeStyles((theme) => ({
  mainGrid: {
    marginTop: theme.spacing(3),
  },
}));

const sections = [
  { title: "Top Universities", url: "http://localhost:3000/UniversityData" },
  { title: "Top Courses", url: "http://localhost:3000/Album" },
  { title: "Upcoming Exams", url: "http://localhost:3000/Exams" },
  { title: "Admission 2021", url: "http://localhost:3000/Data" },
  { title: "eLearning", url: "http://localhost:3000/Album" },
];

const featuredPosts = [
  {
    title: "Engineering",
    date: "Nov 12",
    description:
      "The top Engineering colleges in India as per NIRF 2021 ranking include IIT Madras, IIT Delhi, IIT Bombay etc. There are around 6078 recognized Engineering colleges in India, out of which 4263 colleges/universities offer BTech courses. Some of the best private Engineering colleges in India include Vellore Institute of Technology Vellore, Amrita School of Engineering, Amrita Vishwa Vidyapeetham Coimbatore and many others.",
    image: "https://source.unsplash.com/random",
    imageText: "Image Text",
  },
  {
    title: "Management",
    date: "Nov 11",
    description:
      "This is a wider card with supporting text below as a natural lead-in to additional content.",
    image: "https://source.unsplash.com/random",
    imageText: "Image Text",
  },
  {
    title: "Medical",
    date: "Nov 12",
    description:
      "This is a wider card with supporting text below as a natural lead-in to additional content.",
    image: "https://source.unsplash.com/random",
    imageText: "Image Text",
  },
  {
    title: "Commerce",
    date: "Nov 11",
    description:
      "This is a wider card with supporting text below as a natural lead-in to additional content.",
    image: "https://source.unsplash.com/random",
    imageText: "Image Text",
  },
];

const posts = [post1, post2, post3];

const sidebar = {
  title: "About",
  description:
    "Etiam porta sem malesuada magna mollis euismod. Cras mattis consectetur purus sit amet fermentum. Aenean lacinia bibendum nulla sed consectetur.",
  archives: [
    { title: "March 2020", url: "#" },
    { title: "February 2020", url: "#" },
    { title: "January 2020", url: "#" },
    { title: "November 1999", url: "#" },
    { title: "October 1999", url: "#" },
    { title: "September 1999", url: "#" },
    { title: "August 1999", url: "#" },
    { title: "July 1999", url: "#" },
    { title: "June 1999", url: "#" },
    { title: "May 1999", url: "#" },
    { title: "April 1999", url: "#" },
  ],
  social: [
    { name: "GitHub", icon: GitHubIcon },
    { name: "Twitter", icon: TwitterIcon },
    { name: "Facebook", icon: FacebookIcon },
  ],
};

export default function Blog() {
  const classes = useStyles();

  return (
    <React.Fragment>
      <CssBaseline />
      <Container maxWidth="lg">
        <Header sections={sections} />
        <main>
          {/* <MainFeaturedPost post={mainFeaturedPost} /> */}
          <MainCarousel />
          <Grid container spacing={4}>
            {featuredPosts.map((post) => (
              <FeaturedPost key={post.title} post={post} />
            ))}
          </Grid>
          <Grid container spacing={5} className={classes.mainGrid}>
            {/* <Main title="From the firehose" posts={posts} /> */}
            <Sidebar
              title={sidebar.title}
              description={sidebar.description}
              archives={sidebar.archives}
              social={sidebar.social}
            />
          </Grid>
        </main>
      </Container>
      <Footer />
    </React.Fragment>
  );
}
